mapper.select:
SELECT COUNT(*) FROM log
LEFT JOIN user
ON log.user_id = user.id


        AND log.log_type = #{logType}


        AND log.user_id = #{userId}


        AND log.user_date >= #{startDate}


        AND #{endDate} >= log.user_date

SELECT DATE_FORMAT(log.user_date,'%Y-%m-%d %H:%h:%s') AS user_date,log.log_type,log.log_content,log.user_ip,user.username,user.realname
FROM log
LEFT JOIN user ON
log.user_id = user.id


        AND log.log_type = #{logType}


        AND log.user_id = #{userId}


        AND log.user_date >= #{startDate}


        AND #{endDate} >= log.user_date


ORDER BY log.user_date DESC LIMIT ${begin},${pageSize}
